select RAND(543433)
select RAND(543433)
SELECT RAND(100), RAND(), RAND()

-- example of RAND(), produces four different random numbers that are generated by the RAND function
DECLARE @counter SMALLINT;  
SET @counter = 1;  
WHILE @counter < 5  
   BEGIN  
      SELECT RAND() Random_Number  
      SET @counter = @counter + 1  
   END;  
GO
----------------------
drop table tblTestRand;

create table tblTestRand(id int, first_name varchar(25), last_name varchar(25))

select * from tblTestRand;

-- error: String or binary data would be truncated.
