SET ANSI_WARNINGS OFF;
insert into tblTestRand(id, first_name, last_name)
values(1, 'john', 'abbott');
insert into tblTestRand
values(2, 'david', 'johnson'),(3,'Tom','jerry'),(4,'Jason','San Antonio'),(5,'William','New York'),
(6,'Donna','San Jose'),
(7,'Michelle','Boston');

select * from tblTestRand
order by first_name;

-- newid() to list the rows in a random order
select * from tblTestRand
order by newid();

DECLARE @NmbrPeople INT  
SELECT @NmbrPeople = COUNT(*)  
FROM tblTestRand;  
PRINT 'The number of people as of ' +  
      CAST(GETDATE() AS CHAR(20)) + ' is ' +  
      CAST(@NmbrPeople AS CHAR(10));  
select  CAST(@NmbrPeople AS CHAR(10));
GO

